import javax.crypto.*;
import java.security.*;
import java.security.spec.InvalidKeySpecException;
import java.security.spec.PKCS8EncodedKeySpec;
import java.util.Base64;

public class TestSifra {
    public static void main(String[] args) throws NoSuchPaddingException, NoSuchAlgorithmException, InvalidKeyException, IllegalBlockSizeException, BadPaddingException, InvalidKeySpecException {
        String strKey = "";
        String strMsg = "";

        byte[] bytesKey = Base64.getDecoder().decode(strKey);
        byte[] bytesMsg = Base64.getDecoder().decode(strMsg);

        PKCS8EncodedKeySpec  bobPubKeySpec = new PKCS8EncodedKeySpec(bytesKey);
        KeyFactory keyFactory = KeyFactory.getInstance("RSA");
        PrivateKey privateKey = keyFactory.generatePrivate(bobPubKeySpec);

        Cipher cipher = Cipher.getInstance("RSA");
        cipher.init(Cipher.DECRYPT_MODE,privateKey);
        byte[] byteDecriptedMsg = cipher.doFinal(bytesMsg);

        String decryptedMsg = new String (byteDecriptedMsg);
        System.out.println(decryptedMsg);

    }
}
